// RoomSelector.jsx

import React from 'react';

const RoomSelector = () => {
  // Function to handle room selection
  const handleRoomSelection = (room) => {
    // Add logic to handle the selected room (e.g., update state, trigger actions, etc.)
    console.log(`Selected room: ${room}`);
  };

  return (
    <div className="room-selector-container">
      <h3>Room Selector</h3>
      {/* Room Buttons */}
      <div className="room-buttons">
        <button onClick={() => handleRoomSelection('Kitchen')}>Kitchen</button>
        <button onClick={() => handleRoomSelection('Bathroom')}>Bathroom</button>
        <button onClick={() => handleRoomSelection('Living Room')}>Living Room</button>
        <button onClick={() => handleRoomSelection('Bedroom')}>Bedroom</button>
        {/* Add more buttons as needed */}
      </div>
    </div>
  );
};

export default RoomSelector;
