// GraphicSection.jsx
import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';

const GraphicSection = () => {
  const [selectedFilter, setSelectedFilter] = useState('temperature');

  // Dummy data for the chart (replace with actual data)
  const chartData = {
    labels: ['1 AM', '2 AM', '3 AM', '4 AM', '5 AM'],
    datasets: [
      {
        label: selectedFilter === 'temperature' ? 'Temperature' : selectedFilter === 'humidity' ? 'Humidity' : 'Smoke',
        data: [25, 22, 23, 20, 18], // Replace with your data
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 2,
      },
    ],
  };

  // Options for the chart
  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  // Function to handle filter selection
  const handleFilterChange = (filter) => {
    setSelectedFilter(filter);
  };

  return (
    <div className="graphic-section-container">
      <h3>Graphic Section</h3>

      {/* Filter Selection */}
      <div className="filter-selection">
        <label>Select Filter:</label>
        <select value={selectedFilter} onChange={(e) => handleFilterChange(e.target.value)}>
          <option value="temperature">Temperature</option>
          <option value="humidity">Humidity</option>
          <option value="smoke">Smoke</option>
        </select>
      </div>

      {/* Chart */}
      <div className="chart-container">
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default GraphicSection;
